CREATE TABLE Пользователь (
    PK_id_пользователя INT IDENTITY(1,1) PRIMARY KEY,
    Логин NVARCHAR(50) NOT NULL,
    Email NVARCHAR(100) NOT NULL UNIQUE,
    Аватар VARCHAR(max),
    Дата_рождения DATE,
    Пол VARCHAR(10)
);

CREATE TABLE Получатели_ухода (
    PK_id_получателя INT IDENTITY(1,1) PRIMARY KEY,
    Имя NVARCHAR(100) NOT NULL,
    Аватар VARCHAR(max),
    FK_id_пользователя INT NOT NULL,
    CONSTRAINT FK_Получатели_Пользователь FOREIGN KEY (FK_id_пользователя) REFERENCES Пользователь(PK_id_пользователя)
);

CREATE TABLE Аллергии (
    PK_id_аллергии INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_получателя INT,
	  FK_id_пользователя INT,
    Аллерген NVARCHAR(200) NOT NULL,
    CONSTRAINT FK_Аллергия_Получатель FOREIGN KEY (FK_id_получателя) REFERENCES Получатели_ухода(PK_id_получателя),
    CONSTRAINT FK_Аллергия_Пользователь FOREIGN KEY (FK_id_пользователя) REFERENCES Пользователь(PK_id_пользователя)
);

CREATE TABLE Категория_архива (
    PK_id_категорииАрхива INT IDENTITY(1,1) PRIMARY KEY,
    Название NVARCHAR(100) NOT NULL
);

CREATE TABLE Личный_архив (
    PK_id_личногоАрхива INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_категорииАрхива INT NOT NULL,
	FK_id_пользователя INT,
	FK_id_получателя INT,
    CONSTRAINT FK_ЛичныйАрхив_КатегорияАрхива FOREIGN KEY (FK_id_категорииАрхива) REFERENCES Категория_архива(PK_id_категорииАрхива),
    CONSTRAINT FK_ЛичныйАрхив_Получатель FOREIGN KEY (FK_id_получателя) REFERENCES Получатели_ухода(PK_id_получателя),
    CONSTRAINT FK_ЛичныйАрхив_Пользователь FOREIGN KEY (FK_id_пользователя) REFERENCES Пользователь(PK_id_пользователя)
);

CREATE TABLE Архив_рецептов (
    PK_id_архива INT IDENTITY(1,1) PRIMARY KEY,
    Изображение VARCHAR(max) NOT NULL,
    Дата_загрузки DATETIME NOT NULL DEFAULT GETDATE(),
    FK_id_пользователя INT NOT NULL,
    CONSTRAINT FK_АрхивРецептов_Пользователь FOREIGN KEY (FK_id_пользователя) REFERENCES Пользователь(PK_id_пользователя)
);




CREATE TABLE Категории_отслеживания (
    PK_id_категорииОтслеж INT IDENTITY(1,1) PRIMARY KEY,
    Тип VARCHAR(100) NOT NULL UNIQUE
);

CREATE TABLE Позиция_записи (
    PK_id_позиции INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_пользователя INT NOT NULL,
    FK_id_получателя INT,
    FK_id_категорииОтслеж INT NOT NULL,
    Дата_создания DATETIME NOT NULL DEFAULT GETDATE(),
    Активность BIT NOT NULL DEFAULT 1,
    CONSTRAINT FK_Позиция_Пользователь FOREIGN KEY (FK_id_пользователя) REFERENCES Пользователь(PK_id_пользователя),
    CONSTRAINT FK_Позиция_Получатель FOREIGN KEY (FK_id_получателя) REFERENCES Получатели_ухода(PK_id_получателя),
    CONSTRAINT FK_Позиция_Категория FOREIGN KEY (FK_id_категорииОтслеж) REFERENCES Категории_отслеживания(PK_id_категорииОтслеж)
);




CREATE TABLE Цвет_иконки_лекарств (
    PK_id_ЦветИконки INT IDENTITY(1,1) PRIMARY KEY,
    Цвет VARCHAR(50) NOT NULL UNIQUE
);

CREATE TABLE Вектора_изображений (
    PK_id_вектора INT IDENTITY(1,1) PRIMARY KEY,
    Название VARCHAR(50) UNIQUE,
    Вектор TEXT NOT NULL,
    FK_id_категорииОтслеж INT
    CONSTRAINT FK_Вектора_Категории FOREIGN KEY (FK_id_категорииОтслеж) REFERENCES Категории_отслеживания(PK_id_категорииОтслеж),
);

CREATE TABLE Иконки_лекарств (
    PK_id_иконки INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_вектора INT,
    FK_id_ЦветИконки INT,
    CONSTRAINT FK_Иконки_Вектор FOREIGN KEY (FK_id_вектора) REFERENCES Вектора_изображений(PK_id_вектора),
    CONSTRAINT FK_Иконки_Цвет FOREIGN KEY (FK_id_ЦветИконки) REFERENCES Цвет_иконки_лекарств(PK_id_ЦветИконки)
);

CREATE TABLE Периодичность (
    PK_id_периодичности INT IDENTITY(1,1) PRIMARY KEY,
    Название VARCHAR(100) NOT NULL UNIQUE,
    Период INT NOT NULL
);




CREATE TABLE Способы_приёма (
    PK_id_способаПриёма INT IDENTITY(1,1) PRIMARY KEY,
    Тип VARCHAR(100) NOT NULL UNIQUE
);

CREATE TABLE Состав_лекарства (
    PK_id_состава INT IDENTITY(1,1) PRIMARY KEY,
    Название_составляющей NVARCHAR(200) NOT NULL
);

CREATE TABLE Лекарства (
    PK_id_лекарства INT IDENTITY(1,1) PRIMARY KEY,
    Название NVARCHAR(200) NOT NULL,
    Назначение NVARCHAR(500),
    FK_id_личныйАрхив INT,
    FK_id_позиции INT,
    FK_id_иконки INT NOT NULL,
    FK_id_периодичности INT,
    Фото VARCHAR(max),
    FK_id_способаПриёма INT NOT NULL,
    Приёмов_в_день INT,
    Дозировка INT,
    Комментарий NVARCHAR(500),
    Напоминания_запас BIT NOT NULL DEFAULT 0,
    Мин_запас INT NOT NULL DEFAULT 0,
    Текущий_запас INT NOT NULL DEFAULT 0,
    Длительность_приёма INT,
    CONSTRAINT FK_Лекарства_ЛичныйАрхив FOREIGN KEY (FK_id_личныйАрхив) REFERENCES Личный_архив(PK_id_личногоАрхива),
    CONSTRAINT FK_Лекарства_Позиция FOREIGN KEY (FK_id_позиции) REFERENCES Позиция_записи(PK_id_позиции),
    CONSTRAINT FK_Лекарства_Периодичность FOREIGN KEY (FK_id_периодичности) REFERENCES Периодичность(PK_id_периодичности),
    CONSTRAINT FK_Лекарства_Иконки FOREIGN KEY (FK_id_иконки) REFERENCES Иконки_лекарств(PK_id_иконки),
    CONSTRAINT FK_Лекарства_СпособПриёма FOREIGN KEY (FK_id_способаПриёма) REFERENCES Способы_приёма(PK_id_способаПриёма)
);

CREATE TABLE Состав_в_лекарстве (
    PK_id_составаВлекарстве INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_лекарства INT NOT NULL,
    FK_id_составаЛекарства INT NOT NULL,
    CONSTRAINT FK_СоставВЛекарстве_Лекарство FOREIGN KEY (FK_id_лекарства) REFERENCES Лекарства(PK_id_лекарства),
    CONSTRAINT FK_СоставВЛекарстве_Состав FOREIGN KEY (FK_id_составаЛекарства) REFERENCES Состав_лекарства(PK_id_состава)
);

CREATE TABLE Напоминание_лекарства (
    PK_id_напоминанияЛекарства INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_лекарства INT NOT NULL,
    Часы INT NOT NULL,
    Минуты INT NOT NULL,
    CONSTRAINT FK_НапоминаниеЛекарства_Лекарство FOREIGN KEY (FK_id_лекарства) REFERENCES Лекарства(PK_id_лекарства)
);

CREATE TABLE Фиксация_приёма (
    PK_id_фиксПриём INT IDENTITY(1,1) PRIMARY KEY,
    Дата_приёма DATETIME NOT NULL DEFAULT GETDATE(),
    FK_id_напоминанияЛекарства INT NULL,
    CONSTRAINT FK_Фиксация_НапоминаниеЛекарства FOREIGN KEY (FK_id_напоминанияЛекарства) REFERENCES Напоминание_лекарства(PK_id_напоминанияЛекарства)
);



CREATE TABLE Тип_измерения (
    PK_id_ТипИзмерения INT IDENTITY(1,1) PRIMARY KEY,
    Название VARCHAR(100) NOT NULL UNIQUE,
    ед_измерения NVARCHAR(50) NOT NULL,
    FK_id_вектор INT,
    CONSTRAINT FK_Тип_змерение_Вектор FOREIGN KEY (FK_id_вектор) REFERENCES Вектора_изображений(PK_id_вектора),
);

CREATE TABLE Измерение (
    PK_id_измерения INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_позиции INT NOT NULL,
    FK_id_ТипИзмерения INT NOT NULL,
    FK_id_периодичности INT NOT NULL,
    Записей_в_день INT NOT NULL,
    CONSTRAINT FK_Измерение_Позиция FOREIGN KEY (FK_id_позиции) REFERENCES Позиция_записи(PK_id_позиции),
    CONSTRAINT FK_Измерение_Тип FOREIGN KEY (FK_id_ТипИзмерения) REFERENCES Тип_измерения(PK_id_ТипИзмерения),
    CONSTRAINT FK_Измерение_Периодичность FOREIGN KEY (FK_id_периодичности) REFERENCES Периодичность(PK_id_периодичности)
);

CREATE TABLE Напоминание_измерения (
    PK_id_напоминанияИзмерения INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_измерения INT NOT NULL,
    Часы INT NOT NULL,
    Минуты INT NOT NULL,
    CONSTRAINT FK_НапоминаниеИзмерения_Измерение FOREIGN KEY (FK_id_измерения) REFERENCES Измерение(PK_id_измерения)
);

CREATE TABLE Значения_измерения (
    PK_id_значение_измерения INT IDENTITY(1,1) PRIMARY KEY,
    Дата_записи DATETIME NOT NULL DEFAULT GETDATE(),
    Значение FLOAT NOT NULL,
    Заметка NVARCHAR(500),
    FK_id_напоминанияИзмерения INT NULL,
    CONSTRAINT FK_ЗначенияИзмерения_НапоминаниеИзмерения FOREIGN KEY (FK_id_напоминанияИзмерения) REFERENCES Напоминание_измерения(PK_id_напоминанияИзмерения)
);



CREATE TABLE Симптомы (
    PK_id_симптомы INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_позиции INT NOT NULL,
    FK_id_периодичности INT NOT NULL,
    Название NVARCHAR(200) NOT NULL,
    Записей_в_день INT,
    CONSTRAINT FK_НапоминаниеСимптомы_Позиция FOREIGN KEY (FK_id_позиции) REFERENCES Позиция_записи(PK_id_позиции),
    CONSTRAINT FK_НапоминаниеСимптомы_Периодичность FOREIGN KEY (FK_id_периодичности) REFERENCES Периодичность(PK_id_периодичности)
);

CREATE TABLE Напоминание_симптомы (
    PK_id_напоминанияСимптомы INT IDENTITY(1,1) PRIMARY KEY,
    FK_id_симптомы INT NOT NULL,
    Часы INT NOT NULL,
    Минуты INT NOT NULL,
    CONSTRAINT FK_НапоминаниеСимптомы_Симптрмы FOREIGN KEY (FK_id_симптомы) REFERENCES Симптомы(PK_id_симптомы)
);

CREATE TABLE Зафиксированные_симптомы (
    PK_id_ЗафСимптомы INT IDENTITY(1,1) PRIMARY KEY,
    Дата_записи DATETIME NOT NULL DEFAULT GETDATE(),
    Оценка_самочувствия INT,
    Заметка NVARCHAR(500),
    FK_id_напоминанияСимптомы INT NULL,
    CONSTRAINT FK_ЗафиксированныеСимптомы_НапоминаниеСимптомы FOREIGN KEY (FK_id_напоминанияСимптомы) REFERENCES Напоминание_симптомы(PK_id_напоминанияСимптомы)
);



